PRINT (N'Create or alter view [dbo].[VW_PhoneDetail]')
GO

CREATE OR ALTER VIEW dbo.VW_PhoneDetail
as
    SELECT
        a.Id as IdPhoneDetail,
        B.Id as IdPhone,
        b.PhoneName as PhoneName,
        c.MucUuDai as ReducedAmount,
        d.Name as MaterialName,
        e.Name as RomName,
        f.Name as RamName,
        g.Name as OperatingSystemName,
        h.Name as BatteryName,
        i.Name as SimName,
        k.Name as ChipCPUName,
        l.Name as ChipGPUName,
        n.Name as ColorName,
        p.Name as ChargingportTypeName,
        a.Weight ,
        a.FrontCamera,
        a.Resolution,
        a.Size,
        a.Price,
        a.[Status],
        z.Name as ProductionCompanyName
    FROM PhoneDetailds A LEFT JOIN Phones B on A.IdPhone = B.Id
        left JOIN Discount C ON A.IdDiscount = c.Id
        LEFT JOIN Material D on a.IdMaterial = d.Id
        LEFT JOIN Rom E ON a.IdRom = E.Id
        left JOIN Ram f on a.IdRam = f.Id
        LEFT JOIN OperatingSystem g on a.IdOperatingSystem = g.Id
        LEFT JOIN Battery h on a.IdBattery = h.Id
        LEFT JOIN Sim i on a.IdSim = i.Id
        LEFT JOIN ChipCPUs k on a.IdChipCPU = k.Id
        LEFT JOIN ChipGPUs l on a.IdChipGPU = l.Id
        LEFT JOIN Colors n on a.IdColor = n.id
        LEFT JOIN ChargingportType p on a.IdChargingport = p.Id
        LEFT JOIN ProductionCompany z on b.IdProductionCompany = z.Id
