PRINT (N'Create or alter trigger [deleteIfExits] on table [dbo].[ListImage]')
GO
CREATE OR ALTER TRIGGER dbo.deleteIfExits
ON dbo.ListImage
AFTER INSERT as
BEGIN
    SET NOCOUNT ON;
    IF EXISTS (SELECT *
    from ListImage
    WHERE [Image] in (SELECT [Image]
        from inserted) AND IdPhoneDetaild in (select IdPhoneDetaild
        from inserted) )
    BEGIN
        DELETE from ListImage WHERE [Image] in (SELECT [Image]
            from inserted) AND IdPhoneDetaild in (select IdPhoneDetaild
            from inserted);
          INSERT INTO ListImage SELECT * FROM inserted;
    END
    ELSE
    BEGIN
         INSERT INTO ListImage SELECT * FROM inserted;
    END;
END;
GO
